package lacture7;

public class SumofTwoArrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int [] arr = {1,0,2,9};
		int [] brr = {3,4,5,6,7};
		int  sum =0 ;
		for(int i = 0;i<arr.length;i++)
		{
			sum = arr[i]+brr[i];
			System.out.print(sum+" ");
		}
		

	}

}
